<?php

class NorthAmericanBancard_Config 
{
	public $config = array();
	
	function __construct(){
		   
	   $this->config['applicationprofileid'] = 14644;  // applicationprofileid provided velocity
	   
	   $this->config['merchantprofileid'] = "PrestaShop Global HC"; // merchantprofileid provided velocity
	   
	   $this->config['workflowid'] = 2317000001; // workflowid provided velocity
	   
	   $this->config['baseurl'] = "https://api.cert.nabcommerce.com/REST/2.0.18/"; // baseurl provided velocity
	   
	   $this->config['identitytoken'] = "";
	   
	   $this->config['debug'] = false; // just for test to display the response. 
	
	}
}
$configobj = new NorthAmericanBancard_Config();

/*
 * setups is the methods to set the value at the time of configuration 
 */
 
$apppfid = isset($configobj->config['applicationprofileid']) ? $configobj->config['applicationprofileid'] : null;
$mrhtpfid = isset($configobj->config['merchantprofileid']) ? $configobj->config['merchantprofileid'] : null;
$baseurl = isset($configobj->config['baseurl']) ? $configobj->config['baseurl'] : null;
$identytoken = isset($configobj->config['identitytoken']) ? $configobj->config['identitytoken'] : null;
$workflowid = isset($configobj->config['workflowid']) ? $configobj->config['workflowid'] : null;
$debug = isset($configobj->config['debug']) ? $configobj->config['debug'] : false;

if ($apppfid != null && $mrhtpfid != null && $baseurl != null && $identytoken != null && $workflowid != null) {
	VelocityCon::setups($apppfid, $mrhtpfid, $baseurl, $identytoken, $workflowid, $debug);
} else {
	throw new Exception(NorthAmericanBancard_Message::$descriptions['errcredentialnotset']);
}
